import '../assets/css/job.css';
class StudentEditProfile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      page: props.page,
    iconTabs: 2,
        handleFirstName:'',
        handleLastName:'',
        handleUTRNUMBER:'',
        handleEmail:'',
        handleHomeAddress:'',
        handlePhone:'',
        handleHobies:[],
        handleSkills:[],
        handleAboutU:'',
        handleExpire:'',
        handleCVV:'',
    plainTabs: 2,
        handleCardHolderL:'',
        togglestate:true,
        filter:false,
  };
      this.handleClick = this.handleClick.bind(this);
      this.handleFirstName = this.handleFirstName.bind(this);
